$list = names.uniq
 
def user_interface
    puts " une liste d'username Twitter des journalistes "
    puts " Tapez un des choix proposés dessous "
    puts " 1. Le nombre de journalistes dans la liste "
    puts " 2. Le nombre de handle qui contiennent un numero "
    puts " 3. Le nombre de handle qui contiennent les 4 lettres "
    puts "    du prenom Aude a la suite "
    puts " 4. Combien commencent par une majuscule? "
    puts " 5. Combien contiennent au moins une majuscule apres ? "
    puts " 6. Combien y a-t-il de underscore en tout? "
    puts " 7. Afficher la liste par ordre alphabetique "
    puts " 8. Quels sont les 50 handles les plus courts? "
    puts " 9. Quelle est la position de @epenser? "
    puts " T ou t pour tout voir "
    puts " X ou x Pour terminer le programme "
    puts " Tapez votre réponse : "
    print " > "
    reponse = gets.chomp.to_i
    return reponse
end
 
def user_respons(reponse)
    if reponse == 1
    puts " Le nombre de journalistes est :  "
    journalistes = $list.grep(/[@]/)
    puts "#{journalistes.count}"
   
    elsif reponse == 2
    puts  " Le nombre de handle avec un num est :  "
    handlenumber = $list.grep(/[0-9]/)
    puts "#{handlenumber.count}"
   
    elsif reponse == 3
    puts " Le nombre de handle qui contiennent 4 lettre:  "
    audenumber = $list.grep(/aude/)
    puts "#{audenumber.count}"
   
    elsif reponse == 4
    puts " Il y a    handle qui commencent par une majuscule"
    premiereLettre = $list.grep(/[@]{1}[A-Z]{1}/)
    puts "#{premiereLettre.count}"
   
    elsif reponse == 5
    puts " Il y a   handles qui contiennent une majuscule"
    unemajuscule = $list.grep(/[A-Z]{1}/)
    puts "#{unemajuscule.count}"
   
    elsif reponse == 6
    puts " Il y a    underscore en tout"
    underscore = $list.grep(/[_]{1}/)
    puts "#{underscore.count}"
   
    elsif reponse == 7
    puts " Voici la liste par ordre alphabetique"
    alpha = $list.sort_by { |x| x }
    puts "#{alpha}"
   
    elsif reponse == 8
    puts " Voila les 50 handles les plus courts :   "
    pluscourts =  $list.sort_by {|x| x.length}
    puts "#{pluscourts[0..50]}"
   
    elsif reponse == 9
    puts " il est place a la    position"
    puts $list.find_index('@epenser')
   
    end
end
 
def perform
    reponse = user_interface
    user_respons(reponse)
end
perform
